import DashboardCustomizeRoundedIcon from "@mui/icons-material/DashboardCustomizeRounded";
import KeyRoundedIcon from "@mui/icons-material/KeyRounded";
import QueryStatsRoundedIcon from "@mui/icons-material/QueryStatsRounded";
import SettingsSuggestRoundedIcon from "@mui/icons-material/SettingsSuggestRounded";
import SourceRoundedIcon from "@mui/icons-material/SourceRounded";
import MuiAppBar from "@mui/material/AppBar";
import Avatar from "@mui/material/Avatar";
import Box from "@mui/material/Box";
import CssBaseline from "@mui/material/CssBaseline";
import Drawer from "@mui/material/Drawer";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import { styled, useTheme } from "@mui/material/styles";
import * as React from "react";

const drawerWidth = 240;

export default function SideBar() {
  const theme = useTheme();
  const [open, setOpen] = React.useState(true);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleLogo = () => {
        // redirect to external url
        window.location.href = "https://ashe-docs.vercel.app/";
  };

  return (
    <Box sx={{ display: "flex" }}>
      <CssBaseline />
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          "& .MuiDrawer-paper": {
            width: drawerWidth,
            boxSizing: "border-box",
            backgroundColor: "#1a1a1a",
            color: "#fff",
          },
        }}
        variant="persistent"
        anchor="left"
        open={open}
      >
        <div style={{ height: "10%" }}></div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Avatar
            alt="logo"
            imgProps={{ onClick: handleLogo }}
            src="data:image/svg+xml;base64,"
            sx={{ width: 110, height: 110, cursor: "pointer", padding: "5" }}
          />
        </div>

        <div style={{ height: "10%" }}></div>

        <List>
          {["Source", "Config", "Anonymizer", "Differential Privacy"].map((text, index) => (
            <ListItem key={text} disablePadding>
              <ListItemButton
                onClick={() => {
                    if (index === 0) {
                        window.location.href = "/source"
                    } else if (index === 2) {
                        window.location.href = "/anonymizer"
                    } else if (index === 1) {
                        window.location.href = "/config"
                    } else if (index == 3) {
                        window.location.href = "/dp"
                    }
                }}
              >
                <ListItemIcon sx={{ color: "white" }}>
                    {index === 0 ? (
                        <DashboardCustomizeRoundedIcon />
                    ) : index === 1 ? (
                        <SourceRoundedIcon />
                    ) : index === 2 ? (
                        <KeyRoundedIcon />
                    ) : index === 3 ? (
                        <QueryStatsRoundedIcon />
                    ) : index === 4 ? (
                        <SettingsSuggestRoundedIcon />
                    ) : null}
                </ListItemIcon>
                <ListItemText primary={text} />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Drawer>
    </Box>
  );
}
